<template>
  <div class="demo-wrapper">
    <div class="demo">
      <h1 class="title">多段时间选择器</h1>
      <byte-weektime-picker v-model="byteTime" />
      <p class="value">绑定值：{{byteTime}}</p>
    </div>
    <div class="btn-wrap">
      <button class="btn" @click="welcome()">welcome</button>
      <button class="btn" :disabled="btnDisable" @click="reset()">reset</button>
    </div>
  </div>
</template>

<script>
// 组件内局部引用
import { ByteWeektimePicker } from '../index'

const words = {
  zyc: "",
  num5: "",
  num4: "",
  num3: "",
  num2: "",
  num1: "",
  welcome: ""
}



export default {
  name: 'demo',
  components: {
    ByteWeektimePicker
  },
  data() {
    return {
      byteTime: words.zyc,
      second: 5,
      timer: null,
      time: 1000,
      btnDisable: true
    }
  },
  methods: {
    welcome() {
      this.timer = setInterval(() => {
        if (this.second > 0) {
          this.byteTime = words['num' + this.second];
          this.second--;
        } else {
          this.byteTime = this.second === 0 ? words.welcome : '';
          this.second = this.second === 0 ? -1 : 0;
          this.btnDisable = false;
        }
      }, 1000)
    },
    reset() {
      clearInterval(this.timer);
      this.second = 5;
      this.byteTime = "";
      this.btnDisable = true;
    }
  }
}
</script>

